CREATE TABLE blog (id BIGINT AUTO_INCREMENT, url TEXT NOT NULL, email TEXT NOT NULL, is_thumbnail TINYINT(1) DEFAULT '0' NOT NULL, thumbnail_url TEXT, admin_hash TEXT, public_hash TEXT, vote_sum BIGINT DEFAULT 0 NOT NULL, vote_count BIGINT DEFAULT 0 NOT NULL, created_at DATETIME, updated_at DATETIME, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE vote (id BIGINT AUTO_INCREMENT, blog_id BIGINT NOT NULL, value BIGINT NOT NULL, created_at DATETIME, updated_at DATETIME, INDEX blog_id_idx (blog_id), PRIMARY KEY(id)) ENGINE = INNODB;
ALTER TABLE vote ADD FOREIGN KEY (blog_id) REFERENCES blog(id) ON DELETE CASCADE;
